import { useState, useEffect } from 'react'
import { getPopularMovies } from '../helpers/getPopularMovies'


export const usePopularMovies = () => {

    const [state, setState] = useState({
        data: [],
    });

    useEffect( () => {

        getPopularMovies()
            .then( movie => {

                setState({
                    data: movie,
                });
            })

    }, [])




    return state; // { data:[], loading: true };
}
