import React from 'react'
import './MovieCard.css'
export const MovieCard = ({ id, title, img }) => {
    return (

        <div className="card">
            <img
              src={`https://image.tmdb.org/t/p/w200${img}`}
              alt={`${title} Poster`}
            />

            <div className='card-overlay'>
              < h2>{title}</h2>
              <i className="fa-solid fa-star"></i>
            </div>

        </div>


    );
  };
