import React, { useState } from 'react';
import { MovieGrid } from '../MovieGrid/MovieGrid';
import './pages.css';

const initialState = 1;

export default function Home() {

  const [Page, setPage] = useState(initialState);

  const handlePreviousClick = () => {
    if (Page === 1) {
      return;
    }
    setPage(Page - 1);
  }

  const handleNextClick = () => {
    setPage(Page + 1);
  }

  return (

    <div className='home'>

        <MovieGrid page={Page}/>

        <div className='btn-container'>

          <button onClick={handlePreviousClick} className='button-page'>Previous Page</button>
          <button onClick={handleNextClick} className='button-page'>Next Page</button>

      </div>

    </div>

  );
}
