const baseUrl = 'http://localhost:8080';

const postFavMovies = ( endpoint, data, method ) => {

    const url = `${ baseUrl }/${ endpoint }`;
    console.log(data);
    if ( method === 'POST' ) {
        return fetch(url,{
            method,
            headers: {
                'Content-type': 'application/json'
            },
            body: JSON.stringify( data )
        });
    }
}

export const addFavMovieToDb = async( userName, moviesData) => {


        const resp = await postFavMovies( 'api/addFavMovies', { userName, moviesData}, 'POST' );

        const body = await resp.json();
        if (body.ok){
            console.log(body.user, "fav movies sucessfully uploaded to the db");


        } else {
            console.log("fav movies UNsucessfully uploaded to the db")
        }

}

export const addFavMovie = ( userName, id, img, title ) => {

    addFavMovieToDb(userName, { id, img, title } ); 
    return { type: 'addFavMovie' , payload: { id, img, title }};
}
