export const startLoading = (payload) => ({
    type: 'fetchPopular',
    payload
})

export const getPeliculas = () => {
    return async(dispatch) => {
        const url = `https://api.themoviedb.org/3/movie/popular?api_key=30ed9b7672795045f8fbff2a8390adab&page=${1}`
        const resp = await fetch( url );
        const  { results }  = await resp.json();
        const movies = results.map(results => {
            return {
                id: results.id,
                img: results.poster_path,
                title: results.original_title,

            }
        })
        dispatch( startLoading(movies) );






    }
}
