const baseUrl = 'http://localhost:8080';
const postNewUser = ( endpoint, data, method ) => {
    console.log(data);
    const url = `${ baseUrl }/${ endpoint }`;
    console.log(url);
    if ( method === 'POST' ) {
        fetch(url,{
            method,
            headers: {
                'Content-type': 'application/json'
            },
            body:{...data}
        } ).then(res => res.json()).then(res=>console.log(res)).catch(err=>console.log(err));

    }
}

