import React, { useState } from 'react';
import { MovieGrid } from '../MovieGrid/MovieGrid';
import './pages.css';

const initialState = 1;

export default function Home() {

  const [Page, setPage] = useState(initialState);

  const handleClick = () => {
    setPage(Page + 1);
  }

  return (

    <div className='home'>

        <MovieGrid page={Page}/>

        <div className='btn-container'>

          <button onClick={handleClick} className='btn-next'>Next Page</button>
          <button onClick={handleClick} className='btn-next'>Previous Page</button>

      </div>

    </div>

  );
}
