import React from 'react'

export const MovieCard = ({ id, title, img }) => {
    return (
      <div className="movie-card">
        <div className="overlay">
          <img
            src={`https://image.tmdb.org/t/p/w200${img}`}
            alt={`${title} Poster`}
          />
        </div>


      </div>
    );
  };
